import React, { useState, useEffect } from 'react';
import './Dashboard.css';

const Dashboard = ({ user, onLogout }) => {
  const [activeTab, setActiveTab] = useState('overview');
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [dashboardData, setDashboardData] = useState({
    stats: {
      totalEmployees: 0,
      presentToday: 0,
      onLeave: 0,
      payrollDue: 0
    },
    recentActivity: [],
    attendanceStatus: 'Not checked in',
    leaveBalance: {
      annual: 15,
      sick: 10
    },
    salary: {
      basic: 4500,
      allowances: 500,
      total: 5000
    },
    performance: {
      rating: 4.2,
      lastReviewed: '3 months ago'
    }
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch dashboard data
  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');

      // Fetch statistics
      const statsResponse = await fetch('/api/dashboard/stats', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      // Fetch recent activity
      const activityResponse = await fetch('/api/dashboard/activity', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      // Fetch user-specific data
      const userDataResponse = await fetch('/api/dashboard/user-data', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      const stats = statsResponse.ok ? await statsResponse.json() : { data: dashboardData.stats };
      const activity = activityResponse.ok ? await activityResponse.json() : { data: dashboardData.recentActivity };
      const userData = userDataResponse.ok ? await userDataResponse.json() : { data: {} };

      setDashboardData({
        stats: stats.data || dashboardData.stats,
        recentActivity: activity.data || dashboardData.recentActivity,
        attendanceStatus: userData.data?.attendanceStatus || dashboardData.attendanceStatus,
        leaveBalance: userData.data?.leaveBalance || dashboardData.leaveBalance,
        salary: userData.data?.salary || dashboardData.salary,
        performance: userData.data?.performance || dashboardData.performance
      });
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      setError('Failed to load dashboard data');
    } finally {
      setLoading(false);
    }
  };

  // Check attendance status
  const checkAttendanceStatus = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('/api/attendance/status', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setDashboardData(prev => ({
          ...prev,
          attendanceStatus: data.status
        }));
      }
    } catch (error) {
      console.error('Error checking attendance status:', error);
    }
  };

  // Check in/out
  const handleAttendance = async (action) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('/api/attendance/check', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ action })
      });

      if (response.ok) {
        const data = await response.json();
        setDashboardData(prev => ({
          ...prev,
          attendanceStatus: data.status
        }));
        // Refresh dashboard data
        fetchDashboardData();
      }
    } catch (error) {
      console.error('Error updating attendance:', error);
    }
  };

  useEffect(() => {
    fetchDashboardData();
    checkAttendanceStatus();

    // Refresh data every 5 minutes
    const interval = setInterval(() => {
      fetchDashboardData();
    }, 5 * 60 * 1000);

    return () => clearInterval(interval);
  }, []);

  const menuItems = [
    { id: 'overview', label: 'Overview', icon: 'ðŸ“Š' },
    { id: 'profile', label: 'Profile', icon: 'ðŸ‘¤' },
    { id: 'attendance', label: 'Attendance', icon: 'â°' },
    { id: 'leave', label: 'Leave Management', icon: 'ðŸ–ï¸' },
    { id: 'payroll', label: 'Payroll', icon: 'ðŸ’°' },
    { id: 'performance', label: 'Performance', icon: 'ðŸ“ˆ' },
    ...(user?.role === 'admin' ? [
      { id: 'employees', label: 'Employee Management', icon: 'ðŸ‘¥' },
      { id: 'reports', label: 'Reports', icon: 'ðŸ“‹' }
    ] : [])
  ];

  const renderContent = () => {
    if (loading) {
      return (
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Loading dashboard data...</p>
        </div>
      );
    }

    if (error) {
      return (
        <div className="error-container">
          <p>âš ï¸ {error}</p>
          <button className="btn btn-primary" onClick={fetchDashboardData}>
            Retry
          </button>
        </div>
      );
    }

    switch (activeTab) {
      case 'overview':
        return <OverviewTab user={user} data={dashboardData} />;
      case 'profile':
        return <ProfileTab user={user} />;
      case 'attendance':
        return <AttendanceTab user={user} data={dashboardData} onAttendance={handleAttendance} />;
      case 'leave':
        return <LeaveTab user={user} data={dashboardData} />;
      case 'payroll':
        return <PayrollTab user={user} data={dashboardData} />;
      case 'performance':
        return <PerformanceTab user={user} data={dashboardData} />;
      case 'employees':
        return <EmployeesTab user={user} />;
      case 'reports':
        return <ReportsTab user={user} />;
      default:
        return <OverviewTab user={user} data={dashboardData} />;
    }
  };

  return (
    <div className="dashboard">
      {/* Sidebar */}
      <div className={`sidebar ${sidebarOpen ? 'open' : 'closed'}`}>
        <div className="sidebar-header">
          <h3>ðŸš€ HRMS</h3>
          <button 
            className="sidebar-toggle"
            onClick={() => setSidebarOpen(!sidebarOpen)}
            title={sidebarOpen ? 'Collapse Sidebar' : 'Expand Sidebar'}
          >
            {sidebarOpen ? 'â—€' : 'â–¶'}
          </button>
        </div>

        <nav className="sidebar-nav">
          {menuItems.map(item => (
            <button
              key={item.id}
              className={`nav-item ${activeTab === item.id ? 'active' : ''}`}
              onClick={() => setActiveTab(item.id)}
            >
              <span className="nav-icon">{item.icon}</span>
              {sidebarOpen && <span className="nav-label">{item.label}</span>}
            </button>
          ))}
        </nav>

        <div className="sidebar-footer">
          <div className="user-info">
            <div className="user-avatar">
              {user?.first_name?.charAt(0)}{user?.last_name?.charAt(0)}
            </div>
            {sidebarOpen && (
              <div className="user-details">
                <p className="user-name">{user?.first_name} {user?.last_name}</p>
                <p className="user-role">{user?.role}</p>
              </div>
            )}
          </div>
          <button className="logout-btn" onClick={onLogout}>
            {sidebarOpen ? 'ðŸšª Logout' : 'ðŸšª'}
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="main-content">
        <header className="content-header">
          <div className="header-left">
            <h1>{menuItems.find(item => item.id === activeTab)?.label}</h1>
          </div>
          <div className="header-right">
            <span className="current-time">
              {new Date().toLocaleDateString('en-US', { 
                weekday: 'long', 
                year: 'numeric', 
                month: 'long', 
                day: 'numeric' 
              })}
            </span>
          </div>
        </header>

        <main className="content-area">
          {renderContent()}
        </main>
      </div>
    </div>
  );
};

// Tab Components
const OverviewTab = ({ user, data }) => (
  <div className="overview-tab">
    <div className="stats-grid">
      <div className="stat-card">
        <div className="stat-icon">ðŸ“Š</div>
        <div className="stat-content">
          <h3>Total Employees</h3>
          <p className="stat-number">{data.stats.totalEmployees}</p>
          <p className="stat-change positive">+2 this month</p>
        </div>
      </div>

      <div className="stat-card">
        <div className="stat-icon">â°</div>
        <div className="stat-content">
          <h3>Present Today</h3>
          <p className="stat-number">{data.stats.presentToday}</p>
          <p className="stat-change positive">
            {data.stats.totalEmployees > 0 ? Math.round((data.stats.presentToday / data.stats.totalEmployees) * 100) : 0}% attendance
          </p>
        </div>
      </div>

      <div className="stat-card">
        <div className="stat-icon">ðŸ–ï¸</div>
        <div className="stat-content">
          <h3>On Leave</h3>
          <p className="stat-number">{data.stats.onLeave}</p>
          <p className="stat-change neutral">
            {data.stats.totalEmployees > 0 ? Math.round((data.stats.onLeave / data.stats.totalEmployees) * 100) : 0}% of workforce
          </p>
        </div>
      </div>

      <div className="stat-card">
        <div className="stat-icon">ðŸ’°</div>
        <div className="stat-content">
          <h3>Payroll Due</h3>
          <p className="stat-number">${(data.stats.payrollDue / 1000).toFixed(0)}K</p>
          <p className="stat-change positive">Next week</p>
        </div>
      </div>
    </div>

    <div className="recent-activity">
      <h2>Recent Activity</h2>
      <div className="activity-list">
        {data.recentActivity.length > 0 ? (
          data.recentActivity.map((activity, index) => (
            <div key={index} className="activity-item">
              <div className="activity-icon">{activity.icon}</div>
              <div className="activity-content">
                <p><strong>{activity.user}</strong> {activity.action}</p>
                <span className="activity-time">{activity.time}</span>
              </div>
            </div>
          ))
        ) : (
          <div className="activity-item">
            <div className="activity-icon">âœ…</div>
            <div className="activity-content">
              <p><strong>John Doe</strong> checked in at 9:00 AM</p>
              <span className="activity-time">2 hours ago</span>
            </div>
          </div>
        )}
      </div>
    </div>
  </div>
);

const ProfileTab = ({ user }) => (
  <div className="profile-tab">
    <div className="profile-header">
      <div className="profile-avatar">
        {user?.first_name?.charAt(0)}{user?.last_name?.charAt(0)}
      </div>
      <div className="profile-info">
        <h2>{user?.first_name} {user?.last_name}</h2>
        <p className="profile-role">{user?.role} â€¢ {user?.department}</p>
        <p className="profile-position">{user?.position}</p>
      </div>
    </div>

    <div className="profile-details">
      <div className="detail-section">
        <h3>Personal Information</h3>
        <div className="detail-grid">
          <div className="detail-item">
            <label>Email</label>
            <p>{user?.email}</p>
          </div>
          <div className="detail-item">
            <label>Department</label>
            <p>{user?.department || 'Not assigned'}</p>
          </div>
          <div className="detail-item">
            <label>Position</label>
            <p>{user?.position || 'Not assigned'}</p>
          </div>
          <div className="detail-item">
            <label>Role</label>
            <p className="role-badge">{user?.role}</p>
          </div>
        </div>
      </div>
    </div>
  </div>
);

const AttendanceTab = ({ user, data, onAttendance }) => (
  <div className="attendance-tab">
    <div className="attendance-header">
      <h2>Attendance Management</h2>
      <div className="attendance-actions">
        <button 
          className="btn btn-primary" 
          onClick={() => onAttendance('checkin')}
          disabled={data.attendanceStatus === 'Checked In'}
        >
          Check In
        </button>
        <button 
          className="btn btn-secondary" 
          onClick={() => onAttendance('checkout')}
          disabled={data.attendanceStatus === 'Not checked in'}
        >
          Check Out
        </button>
      </div>
    </div>

    <div className="attendance-status">
      <div className="status-card">
        <h3>Today's Status</h3>
        <p className={`status-text ${data.attendanceStatus === 'Checked In' ? 'checked-in' : ''}`}>
          {data.attendanceStatus}
        </p>
        <p className="status-time">Current time: {new Date().toLocaleTimeString()}</p>
      </div>
    </div>

    <div className="attendance-history">
      <h3>This Week's Attendance</h3>
      <div className="attendance-calendar">
        {['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'].map(day => (
          <div key={day} className="calendar-day">
            <span className="day-name">{day}</span>
            <span className="day-status">-</span>
          </div>
        ))}
      </div>
    </div>
  </div>
);

const LeaveTab = ({ user, data }) => (
  <div className="leave-tab">
    <div className="leave-header">
      <h2>Leave Management</h2>
      <button className="btn btn-primary">Request Leave</button>
    </div>

    <div className="leave-balance">
      <h3>Leave Balance</h3>
      <div className="balance-grid">
        <div className="balance-item">
          <span className="balance-type">Annual Leave</span>
          <span className="balance-count">{data.leaveBalance.annual} days remaining</span>
        </div>
        <div className="balance-item">
          <span className="balance-type">Sick Leave</span>
          <span className="balance-count">{data.leaveBalance.sick} days remaining</span>
        </div>
      </div>
    </div>

    <div className="leave-requests">
      <h3>Recent Requests</h3>
      <p>No recent leave requests</p>
    </div>
  </div>
);

const PayrollTab = ({ user, data }) => (
  <div className="payroll-tab">
    <h2>Payroll Information</h2>
    <div className="payroll-summary">
      <div className="payroll-card">
        <h3>Current Month</h3>
        <p className="salary-amount">${data.salary.total.toLocaleString()}</p>
        <p className="salary-period">{new Date().toLocaleDateString('en-US', { month: 'long', year: 'numeric' })}</p>
      </div>
    </div>

    <div className="payroll-details">
      <h3>Salary Breakdown</h3>
      <div className="breakdown-list">
        <div className="breakdown-item">
          <span>Basic Salary</span>
          <span>${data.salary.basic.toLocaleString()}</span>
        </div>
        <div className="breakdown-item">
          <span>Allowances</span>
          <span>${data.salary.allowances.toLocaleString()}</span>
        </div>
        <div className="breakdown-item total">
          <span>Total</span>
          <span>${data.salary.total.toLocaleString()}</span>
        </div>
      </div>
    </div>
  </div>
);

const PerformanceTab = ({ user, data }) => (
  <div className="performance-tab">
    <h2>Performance Reviews</h2>
    <div className="performance-summary">
      <div className="performance-card">
        <h3>Current Rating</h3>
        <div className="rating">{data.performance.rating}/5</div>
        <p>Last reviewed: {data.performance.lastReviewed}</p>
      </div>
    </div>

    <div className="performance-goals">
      <h3>Goals & Objectives</h3>
      <p>No active goals set</p>
    </div>
  </div>
);

const EmployeesTab = ({ user }) => (
  <div className="employees-tab">
    <div className="employees-header">
      <h2>Employee Management</h2>
      <button className="btn btn-primary">Add Employee</button>
    </div>

    <div className="employees-list">
      <p>Employee management features coming soon...</p>
    </div>
  </div>
);

const ReportsTab = ({ user }) => (
  <div className="reports-tab">
    <h2>Reports & Analytics</h2>
    <div className="reports-grid">
      <div className="report-card">
        <h3>Attendance Report</h3>
        <p>View attendance statistics</p>
      </div>
      <div className="report-card">
        <h3>Leave Report</h3>
        <p>Analyze leave patterns</p>
      </div>
      <div className="report-card">
        <h3>Payroll Report</h3>
        <p>Financial summaries</p>
      </div>
    </div>
  </div>
);

export default Dashboard;
