import React, { useState, useEffect } from 'react';
import './Dashboard.css';
import LeaveManagement from './LeaveManagement';
import EmployeeManagement from './EmployeeManagement';

const Dashboard = ({ user, onLogout, token }) => {
  const [activeTab, setActiveTab] = useState('overview');
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [dashboardData, setDashboardData] = useState({
    stats: {
      totalEmployees: 0,
      presentToday: 0,
      onLeave: 0,
      payrollDue: 0
    },
    recentActivity: [],
    attendanceStatus: 'Not checked in',
    leaveBalance: {
      annual: 15,
      sick: 10
    },
    salary: {
      basic: 4500,
      allowances: 500,
      total: 5000
    },
    performance: {
      rating: 4.2,
      lastReviewed: '3 months ago'
    }
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch dashboard data
  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');

      // Fetch statistics
      const statsResponse = await fetch('/api/dashboard/stats', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      // Fetch recent activity
      const activityResponse = await fetch('/api/dashboard/activity', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      // Fetch user-specific data
      const userDataResponse = await fetch('/api/dashboard/user-data', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      const stats = statsResponse.ok ? await statsResponse.json() : { data: dashboardData.stats };
      const activity = activityResponse.ok ? await activityResponse.json() : { data: dashboardData.recentActivity };
      const userData = userDataResponse.ok ? await userDataResponse.json() : { data: {} };

      setDashboardData({
        stats: stats.data || dashboardData.stats,
        recentActivity: activity.data || dashboardData.recentActivity,
        attendanceStatus: userData.data?.attendanceStatus || dashboardData.attendanceStatus,
        leaveBalance: userData.data?.leaveBalance || dashboardData.leaveBalance,
        salary: userData.data?.salary || dashboardData.salary,
        performance: userData.data?.performance || dashboardData.performance
      });
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      setError('Failed to load dashboard data');
    } finally {
      setLoading(false);
    }
  };

  // Check attendance status
  const checkAttendanceStatus = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('/api/attendance/status', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setDashboardData(prev => ({
          ...prev,
          attendanceStatus: data.status
        }));
      }
    } catch (error) {
      console.error('Error checking attendance status:', error);
    }
  };

  // Check in/out
  const handleAttendance = async (action) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('/api/attendance/check', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ action })
      });

      if (response.ok) {
        const data = await response.json();
        setDashboardData(prev => ({
          ...prev,
          attendanceStatus: data.status
        }));
        // Refresh dashboard data
        fetchDashboardData();
      }
    } catch (error) {
      console.error('Error updating attendance:', error);
    }
  };

  useEffect(() => {
    fetchDashboardData();
    checkAttendanceStatus();

    // Refresh data every 5 minutes
    const interval = setInterval(() => {
      fetchDashboardData();
    }, 5 * 60 * 1000);

    return () => clearInterval(interval);
  }, []);

  const menuItems = [
    { id: 'overview', label: 'Overview', icon: 'ðŸ“Š' },
    { id: 'profile', label: 'Profile', icon: 'ðŸ‘¤' },
    { id: 'attendance', label: 'Attendance', icon: 'â°' },
    { id: 'leave', label: 'Leave Management', icon: 'ðŸ–ï¸' },
    { id: 'payroll', label: 'Payroll', icon: 'ðŸ’°' },
    { id: 'performance', label: 'Performance', icon: 'ðŸ“ˆ' },
    ...(user?.role === 'admin' ? [
      { id: 'employees', label: 'Employee Management', icon: 'ðŸ‘¥' },
      { id: 'reports', label: 'Reports', icon: 'ðŸ“‹' }
    ] : [])
  ];

  const renderContent = () => {
    if (loading) {
      return (
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Loading dashboard data...</p>
        </div>
      );
    }

    if (error) {
      return (
        <div className="error-container">
          <p>âš ï¸ {error}</p>
          <button className="btn btn-primary" onClick={fetchDashboardData}>
            Retry
          </button>
        </div>
      );
    }

    switch (activeTab) {
      case 'overview':
        return <OverviewTab user={user} data={dashboardData} onRefresh={fetchDashboardData} />;
      case 'profile':
        return <ProfileTab user={user} />;
      case 'attendance':
        return <AttendanceTab user={user} data={dashboardData} onAttendance={handleAttendance} />;
      case 'leave':
        return <LeaveTab user={user} data={dashboardData} token={token} />;
      case 'payroll':
        return <PayrollTab user={user} data={dashboardData} />;
      case 'performance':
        return <PerformanceTab user={user} data={dashboardData} />;
      case 'employees':
        return <EmployeesTab user={user} token={token} />;
      case 'reports':
        return <ReportsTab user={user} />;
      default:
        return <OverviewTab user={user} data={dashboardData} onRefresh={fetchDashboardData} />;
    }
  };

  return (
    <div className="dashboard">
      {/* Sidebar */}
      <div className={`sidebar ${sidebarOpen ? 'open' : 'closed'}`}>
        <div className="sidebar-header">
          <h3>ðŸš€ HRMS</h3>
          <button 
            className="sidebar-toggle"
            onClick={() => setSidebarOpen(!sidebarOpen)}
            title={sidebarOpen ? 'Collapse Sidebar' : 'Expand Sidebar'}
          >
            {sidebarOpen ? 'â—€' : 'â–¶'}
          </button>
        </div>

        <nav className="sidebar-nav">
          {menuItems.map(item => (
            <button
              key={item.id}
              className={`nav-item ${activeTab === item.id ? 'active' : ''}`}
              onClick={() => setActiveTab(item.id)}
            >
              <span className="nav-icon">{item.icon}</span>
              {sidebarOpen && <span className="nav-label">{item.label}</span>}
            </button>
          ))}
        </nav>

        <div className="sidebar-footer">
          <button className="logout-btn" onClick={onLogout}>
            {sidebarOpen ? 'ðŸšª Logout' : 'ðŸšª'}
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="main-content">
        <header className="content-header">
          <div className="header-left">
            <h1>{menuItems.find(item => item.id === activeTab)?.label}</h1>
          </div>
          <div className="header-right">
            <span className="current-time">
              {new Date().toLocaleDateString('en-US', { 
                weekday: 'long', 
                year: 'numeric', 
                month: 'long', 
                day: 'numeric' 
              })}
            </span>
          </div>
        </header>

        <main className="content-area">
          {renderContent()}
        </main>
      </div>
    </div>
  );
};

